package tracer

import (
	"reflect"
	"testing"
)

func TestGetMethodIdAndArgsFromInput(t *testing.T) {
	tests := []struct {
		name           string
		input          string
		methodIdWont   string
		methodArgsWont []string
	}{
		{
			name:         "TestGetMethodIdAndArgsFromInput must return methodId and methodArgs",
			input:        "0x249e5a5c000000000000000000000000d3c7eec9daa9b061303cfe54f880978d7d03398700000000000000000000000000000000000000000000021e19e0c9bab240000000000000000000000000000000000000000000000000152d02c7e14af6800000",
			methodIdWont: "0x249e5a5c",
			methodArgsWont: []string{
				"000000000000000000000000d3c7eec9daa9b061303cfe54f880978d7d033987",
				"00000000000000000000000000000000000000000000021e19e0c9bab2400000",
				"00000000000000000000000000000000000000000000152d02c7e14af6800000"},
		},
		{
			name:           "TestGetMethodIdAndArgsFromInput must return only methodArgs",
			input:          "",
			methodIdWont:   "",
			methodArgsWont: []string(nil),
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, got1 := GetMethodIdAndArgsFromInput(tt.input)
			if got != tt.methodIdWont {
				t.Errorf("GetMethodIdAndArgsFromInput() got = %v, methodIdWont %v", got, tt.methodIdWont)
			}
			if !reflect.DeepEqual(got1, tt.methodArgsWont) {
				t.Errorf("GetMethodIdAndArgsFromInput() got1 = %v, methodArgsWont %v", got1, tt.methodArgsWont)
			}
		})
	}
}
